// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "./RewardToken.sol";
import "./LendingPool.sol";

/**
 * @title LiquidityMining
 * @notice Distributes rewards to liquidity providers based on their participation
 */
contract LiquidityMining is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;

    // Reward token
    RewardToken public rewardToken;

    // Reward parameters
    struct RewardPool {
        uint256 rewardRate;           // Rewards per second
        uint256 rewardPerShareStored; // Accumulated rewards per share, scaled by 1e18
        uint256 lastUpdateTime;       // Last time rewards were updated
        uint256 totalStaked;          // Total staked LP tokens
    }

    // Pool info by lending pool
    mapping(address => RewardPool) public rewardPools;

    // User info 
    struct UserInfo {
        uint256 staked;                 // Amount of LP tokens staked
        uint256 rewardPerSharePaid;     // Reward per share already paid
        uint256 rewards;                // Accumulated rewards
    }

    // User info by pool and user
    mapping(address => mapping(address => UserInfo)) public userInfo;

    // Pool configuration
    address[] public activePools;

    // Initialization flag
    bool private initialized;

    // Events
    event PoolAdded(address indexed pool, uint256 rewardRate);
    event PoolRewardRateUpdated(address indexed pool, uint256 rewardRate);
    event Staked(address indexed user, address indexed pool, uint256 amount);
    event Withdrawn(address indexed user, address indexed pool, uint256 amount);
    event RewardPaid(address indexed user, address indexed pool, uint256 reward);

    /**
     * @notice Constructor for implementation contract
     */
    constructor() {
        // Empty constructor for implementation contract
    }

    /**
     * @notice Initialize the liquidity mining contract
     * @param _rewardToken The reward token address
     */
    function initialize(address _rewardToken) external {
        require(!initialized, "Already initialized");
        require(_rewardToken != address(0), "Invalid reward token");

        rewardToken = RewardToken(_rewardToken);
        initialized = true;
        _transferOwnership(msg.sender);
    }

    /**
     * @notice Add a new liquidity pool to the mining program
     * @param _pool The lending pool address
     * @param _rewardRate The reward rate per second
     */
    function addPool(address _pool, uint256 _rewardRate) external onlyOwner {
        require(_pool != address(0), "Invalid pool address");
        require(rewardPools[_pool].lastUpdateTime == 0, "Pool already added");

        rewardPools[_pool] = RewardPool({
            rewardRate: _rewardRate,
            rewardPerShareStored: 0,
            lastUpdateTime: block.timestamp,
            totalStaked: 0
        });

        activePools.push(_pool);

        emit PoolAdded(_pool, _rewardRate);
    }

    /**
     * @notice Update the reward rate for a pool
     * @param _pool The pool address
     * @param _rewardRate The new reward rate
     */
    function setRewardRate(address _pool, uint256 _rewardRate) external onlyOwner {
        require(rewardPools[_pool].lastUpdateTime > 0, "Pool not added");

        updatePool(_pool);
        rewardPools[_pool].rewardRate = _rewardRate;

        emit PoolRewardRateUpdated(_pool, _rewardRate);
    }

    /**
     * @notice Update rewards for a specific pool
     * @param _pool The pool address
     */
    function updatePool(address _pool) public {
        RewardPool storage pool = rewardPools[_pool];
        if (block.timestamp <= pool.lastUpdateTime) {
            return;
        }

        if (pool.totalStaked == 0) {
            pool.lastUpdateTime = block.timestamp;
            return;
        }

        uint256 timeElapsed = block.timestamp - pool.lastUpdateTime;
        uint256 rewards = timeElapsed * pool.rewardRate;

        // Update rewards per share
        pool.rewardPerShareStored += (rewards * 1e18) / pool.totalStaked;
        pool.lastUpdateTime = block.timestamp;
    }

    /**
     * @notice Get pending rewards for a user
     * @param _pool The pool address
     * @param _user The user address
     * @return Pending rewards
     */
    function pendingRewards(address _pool, address _user) public view returns (uint256) {
        RewardPool storage pool = rewardPools[_pool];
        UserInfo storage user = userInfo[_pool][_user];

        uint256 rewardPerShare = pool.rewardPerShareStored;

        if (block.timestamp > pool.lastUpdateTime && pool.totalStaked > 0) {
            uint256 timeElapsed = block.timestamp - pool.lastUpdateTime;
            uint256 rewards = timeElapsed * pool.rewardRate;
            rewardPerShare += (rewards * 1e18) / pool.totalStaked;
        }

        return user.staked * (rewardPerShare - user.rewardPerSharePaid) / 1e18 + user.rewards;
    }

    /**
     * @notice Stake LP tokens to earn rewards
     * @param _pool The pool address
     * @param _amount The amount to stake
     */
    function stake(address _pool, uint256 _amount) external nonReentrant {
        require(_amount > 0, "Cannot stake 0");
        require(rewardPools[_pool].lastUpdateTime > 0, "Pool not active");

        updatePool(_pool);

        RewardPool storage pool = rewardPools[_pool];
        UserInfo storage user = userInfo[_pool][msg.sender];

        // Calculate pending rewards
        if (user.staked > 0) {
            uint256 pending = user.staked * (pool.rewardPerShareStored - user.rewardPerSharePaid) / 1e18;
            user.rewards += pending;
        }

        // Transfer LP tokens from user
        IERC20(_pool).safeTransferFrom(msg.sender, address(this), _amount);

        // Update user stake
        user.staked += _amount;
        user.rewardPerSharePaid = pool.rewardPerShareStored;

        // Update pool total
        pool.totalStaked += _amount;

        emit Staked(msg.sender, _pool, _amount);
    }

    /**
     * @notice Withdraw staked LP tokens
     * @param _pool The pool address
     * @param _amount The amount to withdraw
     */
    function withdraw(address _pool, uint256 _amount) external nonReentrant {
        require(_amount > 0, "Cannot withdraw 0");

        UserInfo storage user = userInfo[_pool][msg.sender];
        require(user.staked >= _amount, "Insufficient staked amount");

        updatePool(_pool);

        RewardPool storage pool = rewardPools[_pool];

        // Calculate pending rewards
        uint256 pending = user.staked * (pool.rewardPerShareStored - user.rewardPerSharePaid) / 1e18;
        user.rewards += pending;

        // Update user stake
        user.staked -= _amount;
        user.rewardPerSharePaid = pool.rewardPerShareStored;

        // Update pool total
        pool.totalStaked -= _amount;

        // Transfer LP tokens back to user
        IERC20(_pool).safeTransfer(msg.sender, _amount);

        emit Withdrawn(msg.sender, _pool, _amount);
    }

    /**
     * @notice Claim pending rewards
     * @param _pool The pool address
     */
    function claim(address _pool) external nonReentrant {
        updatePool(_pool);

        RewardPool storage pool = rewardPools[_pool];
        UserInfo storage user = userInfo[_pool][msg.sender];

        // Calculate pending rewards
        uint256 pending = user.staked * (pool.rewardPerShareStored - user.rewardPerSharePaid) / 1e18;
        uint256 totalRewards = user.rewards + pending;

        // Reset user rewards
        user.rewards = 0;
        user.rewardPerSharePaid = pool.rewardPerShareStored;

        // Mint reward tokens to user
        rewardToken.mint(msg.sender, totalRewards);

        emit RewardPaid(msg.sender, _pool, totalRewards);
    }

    /**
     * @notice Get the number of active pools
     * @return Number of active pools
     */
    function poolLength() external view returns (uint256) {
        return activePools.length;
    }
}

contracts/defi/LiquidityMiningFactory.so.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "./LiquidityMining.sol";
import "./RewardToken.sol";

/**
 * @title LiquidityMiningFactory
 * @notice Factory for deploying liquidity mining programs
 */
contract LiquidityMiningFactory is Ownable {
    using Clones for address;

    // Implementation contracts
    address public liquidityMiningImplementation;
    address public rewardTokenImplementation;

    // Deployed programs
    mapping(address => address) public poolToMiningProgram;
    address[] public allMiningPrograms;

    // Deployed reward tokens
    mapping(address => address) public miningProgramToToken;

    // Events
    event MiningProgramCreated(
        address indexed pool,
        address indexed miningProgram,
        address indexed rewardToken,
        string name,
        string symbol
    );

    /**
     * @notice Constructor
     * @param _liquidityMiningImpl The liquidity mining implementation contract
     * @param _rewardTokenImpl The reward token implementation contract
     */
    constructor(
        address _liquidityMiningImpl,
        address _rewardTokenImpl
    ) {
        require(_liquidityMiningImpl != address(0), "Invalid mining implementation");
        require(_rewardTokenImpl != address(0), "Invalid token implementation");

        liquidityMiningImplementation = _liquidityMiningImpl;
        rewardTokenImplementation = _rewardTokenImpl;
    }

    /**
     * @notice Create a new liquidity mining program for a lending pool
     * @param _pool The lending pool address
     * @param _tokenName The reward token name
     * @param _tokenSymbol The reward token symbol
     * @param _rewardRate The initial reward rate (tokens per second)
     * @param _maxSupply The maximum token supply
     * @param _initialSupply The initial token supply
     * @return miningProgram The address of the deployed mining program
     * @return rewardToken The address of the deployed reward token
     */
    function createMiningProgram(
        address _pool,
        string memory _tokenName,
        string memory _tokenSymbol,
        uint256 _rewardRate,
        uint256 _maxSupply,
        uint256 _initialSupply
    ) external onlyOwner returns (address miningProgram, address rewardToken) {
        // Check if pool already has a mining program
        require(poolToMiningProgram[_pool] == address(0), "Mining program already exists for pool");

        // Deploy reward token
        rewardToken = rewardTokenImplementation.clone();
        RewardToken(rewardToken).initialize(_tokenName, _tokenSymbol, _maxSupply, _initialSupply);

        // Deploy liquidity mining program
        miningProgram = liquidityMiningImplementation.clone();
        LiquidityMining(miningProgram).initialize(rewardToken);

        // Setup reward token ownership
        RewardToken(rewardToken).transferOwnership(miningProgram);

        // Add pool to the mining program
        LiquidityMining(miningProgram).addPool(_pool, _rewardRate);

        // Transfer ownership of the mining program to the caller
        LiquidityMining(miningProgram).transferOwnership(msg.sender);

        // Register the mining program
        poolToMiningProgram[_pool] = miningProgram;
        miningProgramToToken[miningProgram] = rewardToken;
        allMiningPrograms.push(miningProgram);

        emit MiningProgramCreated(_pool, miningProgram, rewardToken, _tokenName, _tokenSymbol);

        return (miningProgram, rewardToken);
    }

    /**
     * @notice Get the total number of mining programs
     * @return The number of mining programs
     */
    function getMiningProgramCount() external view returns (uint256) {
        return allMiningPrograms.length;
    }

    /**
     * @notice Get all mining program addresses
     * @return Array of mining program addresses
     */
    function getAllMiningPrograms() external view returns (address[] memory) {
        return allMiningPrograms;
    }
}

contracts/defi/LoanPoolFactory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/proxy/Clones.sol";
import "./LendingPool.sol";
import "./interfaces/IFeeCollector.sol";

/**
 * @title LoanPoolFactory
 * @notice Factory contract for deploying lending pools
 */
contract LoanPoolFactory is Ownable {
    using Clones for address;

    // Implementation contract address
    address public immutable implementation;

    // Registry of deployed pools
    mapping(address => address) public assetToPools;
    address[] public allPools;

    // Protocol configuration
    address public priceOracle;
    address public interestRateModel;
    address public feeCollector;

    // Events
    event PoolCreated(address indexed asset, address indexed pool, string name, string symbol);
    event PriceOracleUpdated(address indexed oldOracle, address indexed newOracle);
    event InterestRateModelUpdated(address indexed oldModel, address indexed newModel);
    event DebugLog(string message, address sender, address owner);

    constructor(
        address _implementation,
        address _priceOracle,
        address _interestRateModel,
        address _feeCollector
    ) {
        require(_implementation != address(0), "Invalid implementation");
        require(_priceOracle != address(0), "Invalid oracle");
        require(_interestRateModel != address(0), "Invalid rate model");
        require(_feeCollector != address(0), "Invalid fee collector");

        implementation = _implementation;
        priceOracle = _priceOracle;
        interestRateModel = _interestRateModel;
        feeCollector = _feeCollector;
    }

    /**
     * @notice Create a new lending pool for a specific asset
     * @param _asset The underlying asset address (e.g., USDC)
     * @param _name The name of the lending pool
     * @param _symbol The symbol of the lending pool
     * @param _collateralFactorBps The collateral factor in basis points (e.g., 75% = 7500)
     * @param _reserveFactorBps The reserve factor in basis points (e.g., 10% = 1000)
     * @return The address of the newly created pool
     */
    function createLendingPool(
        address _asset,
        string memory _name,
        string memory _symbol,
        uint256 _collateralFactorBps,
        uint256 _reserveFactorBps
    ) external payable onlyOwner returns (address) {
        // Log debug info
        emit DebugLog("Creating pool", msg.sender, owner());

        // Check if pool already exists
        require(assetToPools[_asset] == address(0), "Pool already exists");
        require(_asset != address(0), "Invalid asset address");
        require(_collateralFactorBps <= 10000, "Invalid collateral factor");
        require(_reserveFactorBps <= 10000, "Invalid reserve factor");

        // Collect creation fee
        uint256 fee = IFeeCollector(feeCollector).getPoolCreationFee();
        require(msg.value >= fee, "Insufficient fee");

        // Process fee payment
        if (fee > 0) {
            IFeeCollector(feeCollector).collectPoolCreationFee{value: fee}();
        }

        // Refund excess payment if any
        uint256 excess = msg.value - fee;
        if (excess > 0) {
            (bool success, ) = payable(msg.sender).call{value: excess}("");
            require(success, "Refund failed");
        }

        // Deploy new pool using minimal proxy pattern
        address pool = implementation.clone();

        // Log debug info
        emit DebugLog("Pool cloned", msg.sender, owner());

        // Initialize the pool
        LendingPool(pool).initialize(
            _asset,
            _name,
            _symbol,
            _collateralFactorBps,
            _reserveFactorBps,
            priceOracle,
            interestRateModel,
            feeCollector
        );

        // Log debug info
        emit DebugLog("Pool initialized", msg.sender, owner());

        // Transfer ownership of the pool to the caller (not the factory)
        LendingPool(pool).transferOwnership(msg.sender);

        // Log debug info
        emit DebugLog("Pool ownership transferred", msg.sender, owner());

        // Register pool
        assetToPools[_asset] = pool;
        allPools.push(pool);

        emit PoolCreated(_asset, pool, _name, _symbol);
        return pool;
    }

    /**
     * @notice Update the price oracle address
     * @param _newOracle The new price oracle address
     */
    function setPriceOracle(address _newOracle) external onlyOwner {
        require(_newOracle != address(0), "Invalid oracle address");
        address oldOracle = priceOracle;
        priceOracle = _newOracle;
        emit PriceOracleUpdated(oldOracle, _newOracle);
    }

    /**
     * @notice Update the interest rate model
     * @param _newModel The new interest rate model address
     */
    function setInterestRateModel(address _newModel) external onlyOwner {
        require(_newModel != address(0), "Invalid model address");
        address oldModel = interestRateModel;
        interestRateModel = _newModel;
        emit InterestRateModelUpdated(oldModel, _newModel);
    }

    /**
     * @notice Get the total number of pools
     * @return The number of pools
     */
    function getPoolCount() external view returns (uint256) {
        return allPools.length;
    }

    /**
     * @notice Get all pool addresses
     * @return Array of pool addresses
     */
    function getAllPools() external view returns (address[] memory) {
        return allPools;
    }
}

contracts/defi/RewardToken.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.20;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";

/**
 * @title RewardToken
 * @notice Token used for liquidity mining rewards
 */
contract RewardToken is ERC20, Ownable {
    // Maximum supply cap
    uint256 public MAX_SUPPLY;
    bool private initialized;

    /**
     * @notice Constructor for implementation contract
     */
    constructor() ERC20("", "") {
        // Empty constructor for implementation contract
    }

    /**
     * @notice Initialize the token (used with proxy)
     * @param name Token name
     * @param symbol Token symbol
     * @param maxSupply Maximum token supply
     * @param initialSupply Initial token supply to mint to owner
     */
    function initialize(
        string memory name,
        string memory symbol,
        uint256 maxSupply,
        uint256 initialSupply
    ) external {
        require(!initialized, "Already initialized");
        require(initialSupply <= maxSupply, "Initial supply exceeds max supply");

        _initializeERC20(name, symbol);
        MAX_SUPPLY = maxSupply;
        initialized = true;

        if (initialSupply > 0) {
            _mint(msg.sender, initialSupply);
        }
    }

    /**
     * @notice Internal function to initialize ERC20 name and symbol
     */
    function _initializeERC20(string memory name, string memory symbol) internal {
        // This is a bit of a hack to set name and symbol since ERC20 doesn't have
        // a built-in initialize function. In practice you might use OpenZeppelin's
        // ERC20Upgradeable contract instead.
        bytes32 nameSlot = 0x7ae4f661958fbecc2f77be6b0eb280d2a6f604c9ca56cca41288c048a0e53b71;
        bytes32 symbolSlot = 0xd9d233e9c410a3aa1c4404a397b61fda8e9c4a528434783e3c13b4a95899a29c;

        assembly {
            sstore(nameSlot, name)
            sstore(symbolSlot, symbol)
        }
    }

    /**
     * @notice Mint new tokens
     * @param to Recipient address
     * @param amount Amount to mint
     */
    function mint(address to, uint256 amount) external onlyOwner {
        require(totalSupply() + amount <= MAX_SUPPLY, "Exceeds max supply");
        _mint(to, amount);
    }
}

