","keccak256":"0xf09e68aa0dc6722a25bc46490e8d48ed864466d17313b8a0b254c36b54e49899","license":"MIT"},"src/contracts/TokenFactory_v2.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts/proxy/ERC1967/ERC1967Proxy.sol";
import "./TokenTemplate_v2.sol";

/**
 * @title TokenFactory_v2
 * @notice Factory contract for creating new token instances with presale functionality
 * @dev Uses ERC1967 proxy pattern for upgradeable token instances
 */
contract TokenFactory_v2 is Initializable, OwnableUpgradeable, UUPSUpgradeable, ReentrancyGuardUpgradeable {
    string public constant VERSION = "2.0.0";

    // Deployment fee configuration
    uint256 public deploymentFee;
    mapping(address => uint256) public customDeploymentFees;

    // Platform fee configuration
    uint256 public platformFeePercentage; // Percentage with 2 decimals (e.g., 500 = 5%)
    address public platformFeeRecipient;

    // Platform fee vesting configuration
    uint256 public platformFeeVestingDuration; // Duration in seconds
    uint256 public platformFeeCliffDuration;   // Cliff duration in seconds
    bool public platformFeeVestingEnabled;     // Whether vesting is enabled

    // Enhanced token tracking
    address[] public deployedTokens;
    mapping(address => address[]) private userCreatedTokens;
    mapping(address => mapping(address => bool)) private isUserToken;
    mapping(address => address) private tokenCreator;

    // Events
    event DeploymentFeeUpdated(uint256 newFee);
    event CustomDeploymentFeeSet(address indexed user, uint256 fee);
    event PlatformFeeUpdated(uint256 newPercentage);
    event PlatformFeeRecipientUpdated(address newRecipient);
    event PlatformFeeVestingConfigured(
        uint256 vestingDuration,
        uint256 cliffDuration,
        bool enabled
    );

    event TokenCreated(
        address token,
        string name,
        string symbol,
        address owner,
        uint256 startTime,
        uint256 endTime
    );

    // Template implementation
    TokenTemplate_v2 public immutable tokenImplementation;

    constructor(address _implementation) {
        tokenImplementation = TokenTemplate_v2(_implementation);
    }

    function initialize(uint256 _deploymentFee) public initializer {
        __Ownable_init();
        __UUPSUpgradeable_init();
        __ReentrancyGuard_init();
        deploymentFee = _deploymentFee;
        platformFeePercentage = 500; // Default 5%
        platformFeeRecipient = owner(); // Default to contract owner
        platformFeeVestingDuration = 365 days; // Default 1 year vesting
        platformFeeCliffDuration = 90 days;    // Default 90 days cliff
        platformFeeVestingEnabled = true;      // Enable vesting by default

        emit DeploymentFeeUpdated(_deploymentFee);
        emit PlatformFeeUpdated(platformFeePercentage);
        emit PlatformFeeRecipientUpdated(platformFeeRecipient);
        emit PlatformFeeVestingConfigured(
            platformFeeVestingDuration,
            platformFeeCliffDuration,
            platformFeeVestingEnabled
        );
    }

    /**
     * @notice Configure platform fee vesting parameters
     * @param _vestingDuration Duration of the vesting period in seconds
     * @param _cliffDuration Duration of the cliff period in seconds
     * @param _enabled Whether vesting is enabled
     */
    function configurePlatformFeeVesting(
        uint256 _vestingDuration,
        uint256 _cliffDuration,
        bool _enabled
    ) external onlyOwner {
        require(_cliffDuration <= _vestingDuration, "Cliff longer than vesting");
        platformFeeVestingDuration = _vestingDuration;
        platformFeeCliffDuration = _cliffDuration;
        platformFeeVestingEnabled = _enabled;

        emit PlatformFeeVestingConfigured(
            _vestingDuration,
            _cliffDuration,
            _enabled
        );
    }

    /**
     * @notice Set the platform fee percentage
     * @param _percentage New fee percentage with 2 decimals (e.g., 500 = 5%)
     */
    function setPlatformFeePercentage(uint256 _percentage) external onlyOwner {
        require(_percentage <= 10000, "Fee cannot exceed 100%");
        platformFeePercentage = _percentage;
        emit PlatformFeeUpdated(_percentage);
    }

    /**
     * @notice Set the platform fee recipient
     * @param _recipient Address to receive platform fees
     */
    function setPlatformFeeRecipient(address _recipient) external onlyOwner {
        require(_recipient != address(0), "Invalid recipient");
        platformFeeRecipient = _recipient;
        emit PlatformFeeRecipientUpdated(_recipient);
    }

    /**
     * @notice Set the default deployment fee
     * @param _fee New deployment fee in wei
     */
    function setDeploymentFee(uint256 _fee) external onlyOwner {
        deploymentFee = _fee;
        emit DeploymentFeeUpdated(_fee);
    }

    /**
     * @notice Set a custom deployment fee for a specific address
     * @param _user Address to set custom fee for
     * @param _fee Custom fee in wei (0 for free deployment)
     */
    function setCustomDeploymentFee(address _user, uint256 _fee) external onlyOwner {
        customDeploymentFees[_user] = _fee;
        emit CustomDeploymentFeeSet(_user, _fee);
    }

    /**
     * @notice Get the deployment fee for a specific address
     * @param _user Address to check
     * @return fee The deployment fee in wei
     */
    function getDeploymentFee(address _user) public view returns (uint256) {
        // Check if user has a custom fee
        uint256 customFee = customDeploymentFees[_user];
        if (customFee > 0 || (customFee == 0 && customDeploymentFees[_user] != 0)) {
            return customFee;
        }
        return deploymentFee;
    }

    /**
     * @notice Creates a new token instance with presale configuration
     * @param name Token name
     * @param symbol Token symbol
     * @param initialSupply Initial token supply
     * @param maxSupply Maximum token supply
     * @param enableBlacklist Whether to enable blacklist feature
     * @param enableTimeLock Whether to enable time lock feature
     * @param presaleRate Token price in tokens per ETH
     * @param minContribution Minimum contribution in ETH
     * @param maxContribution Maximum contribution in ETH
     * @param presaleCap Maximum ETH to raise in presale
     * @param startTime Presale start time
     * @param endTime Presale end time
     * @return address The address of the newly created token
     */
    function createToken(
        string memory name,
        string memory symbol,
        uint256 initialSupply,
        uint256 maxSupply,
        bool enableBlacklist,
        bool enableTimeLock,
        uint256 presaleRate,
        uint256 minContribution,
        uint256 maxContribution,
        uint256 presaleCap,
        uint256 startTime,
        uint256 endTime
    ) external payable nonReentrant returns (address) {
        // Check deployment fee
        uint256 fee = getDeploymentFee(msg.sender);
        require(msg.value >= fee, "Insufficient deployment fee");

        // Calculate platform fee tokens
        uint256 platformFeeTokens = (initialSupply * platformFeePercentage) / 10000;
        uint256 adjustedInitialSupply = initialSupply - platformFeeTokens;

        // Create proxy
        bytes memory initData = abi.encodeWithSelector(
            TokenTemplate_v2.initialize.selector,
            TokenTemplate_v2.InitParams({
                name: name,
                symbol: symbol,
                initialSupply: adjustedInitialSupply,
                maxSupply: maxSupply,
                owner: msg.sender,
                enableBlacklist: enableBlacklist,
                enableTimeLock: enableTimeLock,
                presaleRate: presaleRate,
                minContribution: minContribution,
                maxContribution: maxContribution,
                presaleCap: presaleCap,
                startTime: startTime,
                endTime: endTime,
                platformFeeRecipient: platformFeeRecipient,
                platformFeeTokens: platformFeeTokens,
                platformFeeVestingEnabled: platformFeeVestingEnabled,
                platformFeeVestingDuration: platformFeeVestingDuration,
                platformFeeCliffDuration: platformFeeCliffDuration
            })
        );

        ERC1967Proxy proxy = new ERC1967Proxy(
            address(tokenImplementation),
            initData
        );

        address token = address(proxy);

        // Enhanced token tracking
        deployedTokens.push(token);
        userCreatedTokens[msg.sender].push(token);
        isUserToken[msg.sender][token] = true;
        tokenCreator[token] = msg.sender;

        emit TokenCreated(
            token,
            name,
            symbol,
            msg.sender,
            startTime,
            endTime
        );

        return token;
    }

    /**
     * @notice Gets the implementation address
     * @return address The address of the implementation contract
     */
    function getTemplateImplementation() external view returns (address) {
        return address(tokenImplementation);
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}

    /**
     * @notice Gets all deployed tokens
     * @return address[] Array of deployed token addresses
     */
    function getDeployedTokens() external view returns (address[] memory) {
        return deployedTokens;
    }

    /**
     * @notice Withdraw collected fees
     * @dev Only callable by owner
     */
    function withdrawFees() external onlyOwner {
        (bool success, ) = msg.sender.call{value: address(this).balance}("");
        require(success, "Transfer failed");
    }

    // Enhanced user token management functions
    function getUserCreatedTokens(address user) external view returns (address[] memory) {
        return userCreatedTokens[user];
    }

    function isTokenCreator(address user, address token) external view returns (bool) {
        return isUserToken[user][token];
    }

    function getTokenCreator(address token) external view returns (address) {
        return tokenCreator[token];
    }

    function getUserTokenCount(address user) external view returns (uint256) {
        return userCreatedTokens[user].length;
    }
}","keccak256":"0x3040e7141c59882c9763ce3e76dc8ffc69a706e69866977454ff7f885ba84b46","license":"MIT"},"src/contracts/TokenTemplate_v2.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/ERC20PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";

contract TokenTemplate_v2 is 
    Initializable,
    ERC20Upgradeable,
    ERC20BurnableUpgradeable,
    ERC20PausableUpgradeable,
    OwnableUpgradeable,
    UUPSUpgradeable,
    ReentrancyGuardUpgradeable
{
    string public constant VERSION = "2.0.0";

    struct PresaleInfo {
        uint256 softCap;
        uint256 hardCap;
        uint256 minContribution;
        uint256 maxContribution;
        uint256 startTime;
        uint256 endTime;
        uint256 presaleRate;
        bool whitelistEnabled;
        bool finalized;
        uint256 totalContributed;
    }

    // Contract state variables
    PresaleInfo public presaleInfo;
    mapping(address => bool) public whitelist;
    mapping(address => uint256) public contributions;
    uint256 public maxSupply;
    bool public blacklistEnabled;
    bool public timeLockEnabled;
    mapping(address => bool) public blacklist;
    mapping(address => uint256) public timeLocks;

    // Platform fee info
    address public platformFeeRecipient;
    uint256 public platformFeeTokens;
    bool public platformFeeVestingEnabled;
    uint256 public platformFeeVestingDuration;
    uint256 public platformFeeCliffDuration;
    uint256 public platformFeeVestingStart;
    uint256 public platformFeeTokensClaimed;

    // Events
    event PresaleStarted(
        uint256 softCap,
        uint256 hardCap,
        uint256 startTime,
        uint256 endTime,
        uint256 presaleRate
    );
    event WhitelistUpdated(address[] addresses, bool status);
    event BlacklistUpdated(address[] addresses, bool status);
    event TimeLockSet(address account, uint256 unlockTime);
    event ContributionReceived(address contributor, uint256 amount);
    event PresaleFinalized(uint256 totalContributed, uint256 tokensDistributed);
    event ContributionRefunded(address contributor, uint256 amount);
    event PlatformFeeClaimed(uint256 amount);

    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

    struct InitParams {
        string name;
        string symbol;
        uint256 initialSupply;
        uint256 maxSupply;
        address owner;
        bool enableBlacklist;
        bool enableTimeLock;
        uint256 presaleRate;
        uint256 minContribution;
        uint256 maxContribution;
        uint256 presaleCap;
        uint256 startTime;
        uint256 endTime;
        address platformFeeRecipient;
        uint256 platformFeeTokens;
        bool platformFeeVestingEnabled;
        uint256 platformFeeVestingDuration;
        uint256 platformFeeCliffDuration;
    }

    function initialize(InitParams calldata params) public initializer {
        __ERC20_init(params.name, params.symbol);
        __ERC20Burnable_init();
        __ERC20Pausable_init();
        __Ownable_init();
        __ReentrancyGuard_init();
        __UUPSUpgradeable_init();

        require(params.maxSupply >= params.initialSupply, "Max supply must be >= initial supply");
        require(params.startTime > block.timestamp, "Start time must be in future");
        require(params.endTime > params.startTime, "End time must be after start time");

        maxSupply = params.maxSupply;
        blacklistEnabled = params.enableBlacklist;
        timeLockEnabled = params.enableTimeLock;
        platformFeeRecipient = params.platformFeeRecipient;
        platformFeeTokens = params.platformFeeTokens;
        platformFeeVestingEnabled = params.platformFeeVestingEnabled;
        platformFeeVestingDuration = params.platformFeeVestingDuration;
        platformFeeCliffDuration = params.platformFeeCliffDuration;

        // Initialize presale info
        presaleInfo = PresaleInfo({
            softCap: params.presaleCap / 2,
            hardCap: params.presaleCap,
            minContribution: params.minContribution,
            maxContribution: params.maxContribution,
            startTime: params.startTime,
            endTime: params.endTime,
            presaleRate: params.presaleRate,
            whitelistEnabled: false,
            finalized: false,
            totalContributed: 0
        });

        // Mint initial supply to owner
        _mint(params.owner, params.initialSupply);

        // Handle platform fee tokens
        if (params.platformFeeTokens > 0 && params.platformFeeRecipient != address(0)) {
            if (!params.platformFeeVestingEnabled) {
                _mint(params.platformFeeRecipient, params.platformFeeTokens);
            } else {
                platformFeeVestingStart = block.timestamp;
            }
        }

        emit PresaleStarted(
            presaleInfo.softCap,
            presaleInfo.hardCap,
            params.startTime,
            params.endTime,
            params.presaleRate
        );
    }

    function contribute() external payable nonReentrant {
        require(block.timestamp >= presaleInfo.startTime, "Presale not started");
        require(block.timestamp <= presaleInfo.endTime, "Presale ended");
        require(!presaleInfo.finalized, "Presale finalized");
        require(msg.value >= presaleInfo.minContribution, "Below min contribution");
        require(msg.value <= presaleInfo.maxContribution, "Above max contribution");
        require(
            presaleInfo.totalContributed + msg.value <= presaleInfo.hardCap,
            "Hard cap reached"
        );

        if (presaleInfo.whitelistEnabled) {
            require(whitelist[msg.sender], "Not whitelisted");
        }

        uint256 newContribution = contributions[msg.sender] + msg.value;
        require(
            newContribution <= presaleInfo.maxContribution,
            "Would exceed max contribution"
        );

        contributions[msg.sender] = newContribution;
        presaleInfo.totalContributed += msg.value;

        emit ContributionReceived(msg.sender, msg.value);
    }

    function finalize() external onlyOwner nonReentrant {
        require(block.timestamp > presaleInfo.endTime, "Presale not ended");
        require(!presaleInfo.finalized, "Already finalized");
        require(
            presaleInfo.totalContributed >= presaleInfo.softCap,
            "Soft cap not reached"
        );

        presaleInfo.finalized = true;
        uint256 tokensToDistribute = presaleInfo.totalContributed * presaleInfo.presaleRate;

        // Transfer raised funds to owner
        (bool success, ) = owner().call{value: address(this).balance}("");
        require(success, "Transfer failed");

        emit PresaleFinalized(presaleInfo.totalContributed, tokensToDistribute);
    }

    function claimPlatformFeeTokens() external nonReentrant {
        require(msg.sender == platformFeeRecipient, "Not fee recipient");
        require(platformFeeVestingEnabled, "Vesting not enabled");
        require(block.timestamp >= platformFeeVestingStart + platformFeeCliffDuration, "Cliff period not ended");

        uint256 vestedAmount = _calculateVestedAmount();
        uint256 claimableAmount = vestedAmount - platformFeeTokensClaimed;
        require(claimableAmount > 0, "No tokens to claim");

        platformFeeTokensClaimed += claimableAmount;
        _mint(platformFeeRecipient, claimableAmount);

        emit PlatformFeeClaimed(claimableAmount);
    }

    function _calculateVestedAmount() internal view returns (uint256) {
        if (block.timestamp < platformFeeVestingStart + platformFeeCliffDuration) {
            return 0;
        }

        if (block.timestamp >= platformFeeVestingStart + platformFeeVestingDuration) {
            return platformFeeTokens;
        }

        uint256 timeFromCliff = block.timestamp - (platformFeeVestingStart + platformFeeCliffDuration);
        uint256 vestingTime = platformFeeVestingDuration - platformFeeCliffDuration;

        return (platformFeeTokens * timeFromCliff) / vestingTime;
    }

    function getVestingInfo() external view returns (
        uint256 totalTokens,
        uint256 claimed,
        uint256 claimable,
        uint256 vestingEnd
    ) {
        totalTokens = platformFeeTokens;
        claimed = platformFeeTokensClaimed;
        claimable = platformFeeVestingEnabled ? 
            _calculateVestedAmount() - platformFeeTokensClaimed : 
            platformFeeTokens - platformFeeTokensClaimed;
        vestingEnd = platformFeeVestingStart + platformFeeVestingDuration;
    }

    function updateWhitelist(address[] calldata addresses, bool status) external onlyOwner {
        require(presaleInfo.whitelistEnabled, "Whitelist not enabled");
        for (uint256 i = 0; i < addresses.length; i++) {
            whitelist[addresses[i]] = status;
        }
        emit WhitelistUpdated(addresses, status);
    }

    function updateBlacklist(address[] calldata addresses, bool status) external onlyOwner {
        require(blacklistEnabled, "Blacklist not enabled");
        for (uint256 i = 0; i < addresses.length; i++) {
            blacklist[addresses[i]] = status;
        }
        emit BlacklistUpdated(addresses, status);
    }

    function setTimeLock(address account, uint256 unlockTime) external onlyOwner {
        require(timeLockEnabled, "Time lock not enabled");
        require(unlockTime > block.timestamp, "Unlock time must be in future");
        timeLocks[account] = unlockTime;
        emit TimeLockSet(account, unlockTime);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override(ERC20Upgradeable, ERC20PausableUpgradeable) {
        super._beforeTokenTransfer(from, to, amount);

        if (blacklistEnabled) {
            require(!blacklist[from] && !blacklist[to], "Address is blacklisted");
        }

        if (timeLockEnabled && from != address(0)) {
            require(block.timestamp >= timeLocks[from], "Tokens are time-locked");
        }
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function _authorizeUpgrade(address newImplementation) internal override onlyOwner {}
