// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

/**
 * @title IV4TokenBase
 * @dev Interface for the V4 token base
 */
interface IV4TokenBase {
    /**
     * @dev Add a module to the token
     * @param moduleAddress Address of the module to add
     * @param data Initialization data for the module
     * @return success Whether the module was successfully added
     */
    function addModule(address moduleAddress, bytes calldata data) external returns (bool success);

    /**
     * @dev Remove a module from the token
     * @param moduleAddress Address of the module to remove
     * @return success Whether the module was successfully removed
     */
    function removeModule(address moduleAddress) external returns (bool success);

    /**
     * @dev Check if a module is active
     * @param moduleAddress Address of the module to check
     * @return isActive Whether the module is active
     */
    function isModuleActive(address moduleAddress) external view returns (bool isActive);

    /**
     * @dev Get all active modules
     * @return modules Array of active module addresses
     */
    function getModules() external view returns (address[] memory modules);

    /**
     * @dev Execute a function call from a module
     * @param target Target address for the call
     * @param data Call data
     * @return success Whether the call was successful
     * @return returnData Data returned by the call
     */
    function executeFromModule(address target, bytes calldata data) external returns (bool success, bytes memory returnData);

    /**
     * @dev Pause the token
     */
    function pause() external;

    /**
     * @dev Unpause the token
     */
    function unpause() external;

    /**
     * @dev Transfer ownership of the token
     * @param newOwner Address of the new owner
     */
    function transferOwnership(address newOwner) external;
}

src/contracts/V4DistributionModule.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "./interfaces/IV4DistributionModule.sol";
import "./interfaces/IV4Module.sol";
import "./interfaces/IV4TokenBase.sol";

/**
 * @title V4DistributionModule
 * @dev Implementation of distribution module with allocation capabilities
 */
contract V4DistributionModule is 
    Initializable, 
    OwnableUpgradeable, 
    IV4DistributionModule,
    IV4Module
{
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    // Token this module is attached to
    address private _token;

    // Allocation tracking
    EnumerableSetUpgradeable.AddressSet private _allocationWallets;
    mapping(address => Allocation) private _allocations;
    uint256 private _totalAllocated;
    bool private _distributionExecuted;

    // Preset definitions
    struct Preset {
        string name;
        uint256[] ratios;
        string[] labels;
        bool active;
    }

    mapping(uint256 => Preset) private _presets;
    uint256[] private _presetIds;

    // Version info
    string public constant VERSION = "4.0.0";

    // Events
    event AllocationAdded(address indexed wallet, uint256 amount, string label, bool locked, uint256 unlockTime);
    event AllocationRemoved(address indexed wallet);
    event AllocationModified(address indexed wallet, uint256 amount, string label, bool locked, uint256 unlockTime);
    event PresetApplied(uint256 indexed presetId, uint256 totalSupply);
    event DistributionExecuted(uint256 totalDistributed, uint256 walletCount);
    event TokensUnlocked(address indexed wallet, uint256 amount);

    /**
     * @dev Constructor that disables initialization on the implementation contract
     */
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize the distribution module
     * @param tokenAddress Address of the token this module is attached to
     * @param owner Address of the initial owner
     */
    function initialize(address tokenAddress, address owner) public override initializer {
        __Ownable_init();
        _transferOwnership(owner);

        require(tokenAddress != address(0), "V4DistributionModule: token address cannot be zero");
        _token = tokenAddress;

        // Initialize presets
        _initializePresets();
    }

    /**
     * @dev Initialize the module
     * @param tokenAddress Address of the token this module is attached to
     * @param data Additional initialization data (owner address)
     */
    function initialize(address tokenAddress, bytes calldata data) external override initializer {
        require(data.length == 32, "V4DistributionModule: invalid initialization data");
        address owner = address(uint160(uint256(bytes32(data))));
        initialize(tokenAddress, owner);
    }

    /**
     * @dev Get the type of this module
     * @return moduleType Type identifier for this module
     */
    function getModuleType() external pure override returns (bytes32 moduleType) {
        return keccak256("DISTRIBUTION_MODULE");
    }

    /**
     * @dev Check if this module has a specific function
     * @param functionSig Function signature to check for
     * @return hasFunction Whether the module implements the function
     */
    function supportsFunction(bytes4 functionSig) external pure override returns (bool hasFunction) {
        return 
            functionSig == IV4DistributionModule.addAllocation.selector ||
            functionSig == IV4DistributionModule.addMultipleAllocations.selector ||
            functionSig == IV4DistributionModule.applyPreset.selector ||
            functionSig == IV4DistributionModule.removeAllocation.selector ||
            functionSig == IV4DistributionModule.modifyAllocation.selector ||
            functionSig == IV4DistributionModule.getAllAllocations.selector ||
            functionSig == IV4DistributionModule.getAllocation.selector ||
            functionSig == IV4DistributionModule.getAvailablePresets.selector ||
            functionSig == IV4DistributionModule.getPresetDetails.selector ||
            functionSig == IV4DistributionModule.executeDistribution.selector ||
            functionSig == IV4DistributionModule.unlockTokens.selector;
    }

    /**
     * @dev Get the token this module is attached to
     * @return tokenAddress Address of the token
     */
    function getToken() external view override returns (address tokenAddress) {
        return _token;
    }

    /**
     * @dev Add a new allocation
     * @param wallet Recipient wallet address
     * @param amount Amount of tokens to allocate
     * @param label Optional label for this allocation (e.g. "Team", "Marketing")
     * @param locked Whether the allocation should be time-locked
     * @param unlockTime Timestamp when tokens become unlocked (0 if not locked)
     * @return success Whether the allocation was successfully added
     */
    function addAllocation(
        address wallet, 
        uint256 amount, 
        string calldata label,
        bool locked,
        uint256 unlockTime
    ) external override onlyOwner returns (bool success) {
        require(!_distributionExecuted, "V4DistributionModule: distribution already executed");
        require(wallet != address(0), "V4DistributionModule: wallet cannot be zero address");
        require(amount > 0, "V4DistributionModule: amount must be greater than zero");
        require(!_allocationWallets.contains(wallet), "V4DistributionModule: wallet already has allocation");

        if (locked) {
            require(unlockTime > block.timestamp, "V4DistributionModule: unlock time must be in the future");
        }

        // Create and store the allocation
        Allocation memory allocation = Allocation({
            wallet: wallet,
            amount: amount,
            label: label,
            locked: locked,
            unlockTime: locked ? unlockTime : 0
        });

        _allocations[wallet] = allocation;
        _allocationWallets.add(wallet);
        _totalAllocated += amount;

        emit AllocationAdded(wallet, amount, label, locked, unlockTime);
        return true;
    }

    /**
     * @dev Add multiple allocations at once
     * @param wallets Array of recipient wallet addresses
     * @param amounts Array of token amounts
     * @param labels Array of labels
     * @param lockStatus Array of lock statuses
     * @param unlockTimes Array of unlock times
     * @return success Whether all allocations were successfully added
     */
    function addMultipleAllocations(
        address[] calldata wallets,
        uint256[] calldata amounts,
        string[] calldata labels,
        bool[] calldata lockStatus,
        uint256[] calldata unlockTimes
    ) external override onlyOwner returns (bool success) {
        require(!_distributionExecuted, "V4DistributionModule: distribution already executed");
        require(
            wallets.length == amounts.length && 
            wallets.length == labels.length && 
            wallets.length == lockStatus.length && 
            wallets.length == unlockTimes.length,
            "V4DistributionModule: array lengths must match"
        );

        for (uint256 i = 0; i < wallets.length; i++) {
            require(wallets[i] != address(0), "V4DistributionModule: wallet cannot be zero address");
            require(amounts[i] > 0, "V4DistributionModule: amount must be greater than zero");
            require(!_allocationWallets.contains(wallets[i]), "V4DistributionModule: wallet already has allocation");

            if (lockStatus[i]) {
                require(unlockTimes[i] > block.timestamp, "V4DistributionModule: unlock time must be in the future");
            }

            // Create and store the allocation
            Allocation memory allocation = Allocation({
                wallet: wallets[i],
                amount: amounts[i],
                label: labels[i],
                locked: lockStatus[i],
                unlockTime: lockStatus[i] ? unlockTimes[i] : 0
            });

            _allocations[wallets[i]] = allocation;
            _allocationWallets.add(wallets[i]);
            _totalAllocated += amounts[i];

            emit AllocationAdded(wallets[i], amounts[i], labels[i], lockStatus[i], unlockTimes[i]);
        }

        return true;
    }

    /**
     * @dev Apply a preset distribution
     * @param presetId Identifier for the distribution preset
     * @param totalSupply Total supply to distribute
     * @return success Whether the preset was successfully applied
     */
    function applyPreset(
        uint256 presetId,
        uint256 totalSupply
    ) external override onlyOwner returns (bool success) {
        require(!_distributionExecuted, "V4DistributionModule: distribution already executed");
        require(_presets[presetId].active, "V4DistributionModule: preset does not exist");

        // Clear any existing allocations
        _clearAllocations();

        Preset storage preset = _presets[presetId];
        uint256 totalRatio = 0;

        // Calculate total ratio
        for (uint256 i = 0; i < preset.ratios.length; i++) {
            totalRatio += preset.ratios[i];
        }

        require(totalRatio > 0, "V4DistributionModule: total ratio must be greater than zero");

        // Create allocations based on preset ratios
        for (uint256 i = 0; i < preset.ratios.length; i++) {
            // Skip zero ratios
            if (preset.ratios[i] == 0) continue;

            // Calculate amount based on ratio
            uint256 amount = (totalSupply * preset.ratios[i]) / totalRatio;

            // Create a placeholder allocation - user will need to set the actual wallet addresses
            address placeholderWallet = address(uint160(uint256(keccak256(abi.encodePacked(presetId, i)))));

            Allocation memory allocation = Allocation({
                wallet: placeholderWallet,
                amount: amount,
                label: preset.labels[i],
                locked: false,
                unlockTime: 0
            });

            _allocations[placeholderWallet] = allocation;
            _allocationWallets.add(placeholderWallet);
            _totalAllocated += amount;

            emit AllocationAdded(placeholderWallet, amount, preset.labels[i], false, 0);
        }

        emit PresetApplied(presetId, totalSupply);
        return true;
    }

    /**
     * @dev Remove an allocation
     * @param wallet Recipient wallet address to remove
     * @return success Whether the allocation was successfully removed
     */
    function removeAllocation(address wallet) external override onlyOwner returns (bool success) {
        require(!_distributionExecuted, "V4DistributionModule: distribution already executed");
        require(_allocationWallets.contains(wallet), "V4DistributionModule: wallet does not have allocation");

        uint256 amount = _allocations[wallet].amount;
        _totalAllocated -= amount;

        delete _allocations[wallet];
        _allocationWallets.remove(wallet);

        emit AllocationRemoved(wallet);
        return true;
    }

    /**
     * @dev Modify an existing allocation
     * @param wallet Recipient wallet address
     * @param amount New amount of tokens
     * @param label New label
     * @param locked New lock status
     * @param unlockTime New unlock time
     * @return success Whether the allocation was successfully modified
     */
    function modifyAllocation(
        address wallet,
        uint256 amount,
        string calldata label,
        bool locked,
        uint256 unlockTime
    ) external override onlyOwner returns (bool success) {
        require(!_distributionExecuted, "V4DistributionModule: distribution already executed");
        require(_allocationWallets.contains(wallet), "V4DistributionModule: wallet does not have allocation");
        require(amount > 0, "V4DistributionModule: amount must be greater than zero");

        if (locked) {
            require(unlockTime > block.timestamp, "V4DistributionModule: unlock time must be in the future");
        }

        // Update total allocated amount
        _totalAllocated = _totalAllocated - _allocations[wallet].amount + amount;

        // Update the allocation
        _allocations[wallet] = Allocation({
            wallet: wallet,
            amount: amount,
            label: label,
            locked: locked,
            unlockTime: locked ? unlockTime : 0
        });

        emit AllocationModified(wallet, amount, label, locked, unlockTime);
        return true;
    }

    /**
     * @dev Get all allocations
     * @return allocations Array of allocation details
     */
    function getAllAllocations() external view override returns (Allocation[] memory allocations) {
        uint256 count = _allocationWallets.length();
        allocations = new Allocation[](count);

        for (uint256 i = 0; i < count; i++) {
            address wallet = _allocationWallets.at(i);
            allocations[i] = _allocations[wallet];
        }

        return allocations;
    }

    /**
     * @dev Get allocation for a specific wallet
     * @param wallet Recipient wallet address
     * @return allocation The allocation details
     */
    function getAllocation(address wallet) external view override returns (Allocation memory allocation) {
        require(_allocationWallets.contains(wallet), "V4DistributionModule: wallet does not have allocation");
        return _allocations[wallet];
    }

    /**
     * @dev Get available presets
     * @return presetIds Array of preset IDs
     * @return presetNames Array of preset names
     */
    function getAvailablePresets() external view override returns (uint256[] memory presetIds, string[] memory presetNames) {
        uint256 activeCount = 0;

        // Count active presets
        for (uint256 i = 0; i < _presetIds.length; i++) {
            if (_presets[_presetIds[i]].active) {
                activeCount++;
            }
        }

        presetIds = new uint256[](activeCount);
        presetNames = new string[](activeCount);

        uint256 index = 0;
        for (uint256 i = 0; i < _presetIds.length; i++) {
            uint256 presetId = _presetIds[i];
            if (_presets[presetId].active) {
                presetIds[index] = presetId;
                presetNames[index] = _presets[presetId].name;
                index++;
            }
        }

        return (presetIds, presetNames);
    }

    /**
     * @dev Get preset details
     * @param presetId Identifier for the preset
     * @return walletRatios Array of ratios for different wallet types
     * @return labels Array of labels for the wallet types
     */
    function getPresetDetails(uint256 presetId) external view override returns (uint256[] memory walletRatios, string[] memory labels) {
        require(_presets[presetId].active, "V4DistributionModule: preset does not exist");

        Preset storage preset = _presets[presetId];
        return (preset.ratios, preset.labels);
    }

    /**
     * @dev Execute the distribution
     * @return success Whether the distribution was successfully executed
     */
    function executeDistribution() external override onlyOwner returns (bool success) {
        require(!_distributionExecuted, "V4DistributionModule: distribution already executed");
        require(_allocationWallets.length() > 0, "V4DistributionModule: no allocations to distribute");

        _distributionExecuted = true;
        uint256 walletCount = _allocationWallets.length();

        // Transfer tokens to each wallet
        for (uint256 i = 0; i < walletCount; i++) {
            address wallet = _allocationWallets.at(i);
            Allocation storage allocation = _allocations[wallet];

            // If tokens are locked, they stay in this contract until unlocked
            if (allocation.locked) {
                // Request tokens from the token contract to this module
                bytes memory mintData = abi.encodeWithSignature(
                    "mint(address,uint256)",
                    address(this),
                    allocation.amount
                );
                IV4TokenBase(_token).executeFromModule(_token, mintData);
            } else {
                // Mint directly to the recipient
                bytes memory mintData = abi.encodeWithSignature(
                    "mint(address,uint256)",
                    wallet,
                    allocation.amount
                );
                IV4TokenBase(_token).executeFromModule(_token, mintData);
            }
        }

        emit DistributionExecuted(_totalAllocated, walletCount);
        return true;
    }

    /**
     * @dev Unlock tokens for a wallet if the unlock time has passed
     * @param wallet Address of the wallet to unlock tokens for
     * @return success Whether the tokens were successfully unlocked
     */
    function unlockTokens(address wallet) external override returns (bool success) {
        require(_distributionExecuted, "V4DistributionModule: distribution not yet executed");
        require(_allocationWallets.contains(wallet), "V4DistributionModule: wallet does not have allocation");

        Allocation storage allocation = _allocations[wallet];
        require(allocation.locked, "V4DistributionModule: allocation is not locked");
        require(block.timestamp >= allocation.unlockTime, "V4DistributionModule: tokens are still locked");

        // Transfer tokens to the recipient
        (bool transferSuccess,) = _token.call(
            abi.encodeWithSignature(
                "transfer(address,uint256)",
                wallet,
                allocation.amount
            )
        );
        require(transferSuccess, "V4DistributionModule: token transfer failed");

        // Mark as unlocked
        allocation.locked = false;

        emit TokensUnlocked(wallet, allocation.amount);
        return true;
    }

    /**
     * @dev Initialize the preset distributions
     */
    function _initializePresets() private {
        // Preset 1: Standard ICO Distribution
        uint256 presetId = 1;
        _presets[presetId] = Preset({
            name: "Standard ICO",
            ratios: new uint256[](5),
            labels: new string[](5),
            active: true
        });

        _presets[presetId].ratios[0] = 50; // 50% Public Sale
        _presets[presetId].ratios[1] = 20; // 20% Team
        _presets[presetId].ratios[2] = 15; // 15% Reserve
        _presets[presetId].ratios[3] = 10; // 10% Marketing
        _presets[presetId].ratios[4] = 5;  // 5% Advisors

        _presets[presetId].labels[0] = "Public Sale";
        _presets[presetId].labels[1] = "Team";
        _presets[presetId].labels[2] = "Reserve";
        _presets[presetId].labels[3] = "Marketing";
        _presets[presetId].labels[4] = "Advisors";

        _presetIds.push(presetId);

        // Preset 2: Community Focused
        presetId = 2;
        _presets[presetId] = Preset({
            name: "Community Focused",
            ratios: new uint256[](6),
            labels: new string[](6),
            active: true
        });

        _presets[presetId].ratios[0] = 40; // 40% Community Rewards
        _presets[presetId].ratios[1] = 20; // 20% Public Sale
        _presets[presetId].ratios[2] = 15; // 15% Team
        _presets[presetId].ratios[3] = 10; // 10% Development
        _presets[presetId].ratios[4] = 10; // 10% Marketing
        _presets[presetId].ratios[5] = 5;  // 5% Advisors

        _presets[presetId].labels[0] = "Community Rewards";
        _presets[presetId].labels[1] = "Public Sale";
        _presets[presetId].labels[2] = "Team";
        _presets[presetId].labels[3] = "Development";
        _presets[presetId].labels[4] = "Marketing";
        _presets[presetId].labels[5] = "Advisors";

        _presetIds.push(presetId);

        // Preset 3: DeFi Project
        presetId = 3;
        _presets[presetId] = Preset({
            name: "DeFi Project",
            ratios: new uint256[](7),
            labels: new string[](7),
            active: true
        });

        _presets[presetId].ratios[0] = 30; // 30% Liquidity Provision
        _presets[presetId].ratios[1] = 25; // 25% Community Incentives
        _presets[presetId].ratios[2] = 15; // 15% Team
        _presets[presetId].ratios[3] = 10; // 10% Public Sale
        _presets[presetId].ratios[4] = 10; // 10% Development
        _presets[presetId].ratios[5] = 5;  // 5% Marketing
        _presets[presetId].ratios[6] = 5;  // 5% Partnerships

        _presets[presetId].labels[0] = "Liquidity Provision";
        _presets[presetId].labels[1] = "Community Incentives";
        _presets[presetId].labels[2] = "Team";
        _presets[presetId].labels[3] = "Public Sale";
        _presets[presetId].labels[4] = "Development";
        _presets[presetId].labels[5] = "Marketing";
        _presets[presetId].labels[6] = "Partnerships";

        _presetIds.push(presetId);

        // Preset 4: Equal Distribution
        presetId = 4;
        _presets[presetId] = Preset({
            name: "Equal Distribution",
            ratios: new uint256[](4),
            labels: new string[](4),
            active: true
        });

        _presets[presetId].ratios[0] = 25; // 25% Public
        _presets[presetId].ratios[1] = 25; // 25% Team
        _presets[presetId].ratios[2] = 25; // 25% Development
        _presets[presetId].ratios[3] = 25; // 25% Marketing

        _presets[presetId].labels[0] = "Public";
        _presets[presetId].labels[1] = "Team";
        _presets[presetId].labels[2] = "Development";
        _presets[presetId].labels[3] = "Marketing";

        _presetIds.push(presetId);
    }

    /**
     * @dev Clear all existing allocations
     */
    function _clearAllocations() private {
        uint256 count = _allocationWallets.length();

        for (uint256 i = 0; i < count; i++) {
            address wallet = _allocationWallets.at(0); // Always remove the first one
            delete _allocations[wallet];
            _allocationWallets.remove(wallet);

            emit AllocationRemoved(wallet);
        }

        _totalAllocated = 0;
    }
}

src/contracts/V4Factory.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import "@openzeppelin/contracts/proxy/beacon/BeaconProxy.sol";
import "@openzeppelin/contracts/proxy/beacon/UpgradeableBeacon.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "./interfaces/IV4TokenBase.sol";
import "./V4TokenBase.sol";
import "./V4SecurityModule.sol";
import "./V4DistributionModule.sol";

/**
 * @title V4Factory
 * @dev Factory for creating V4 tokens with modular architecture
 */
contract V4Factory is Ownable {
    // Implementation addresses
    address public tokenImplementation;
    address public securityModuleImplementation;
    address public distributionModuleImplementation;

    // Beacon contracts for upgrades
    UpgradeableBeacon public tokenBeacon;
    UpgradeableBeacon public securityModuleBeacon;
    UpgradeableBeacon public distributionModuleBeacon;

    // Token registry
    mapping(address => bool) public isV4Token;
    address[] public allTokens;

    // Version info
    string public constant VERSION = "4.0.0";

    // Events
    event TokenCreated(address indexed tokenAddress, string name, string symbol, address indexed owner);
    event ImplementationUpgraded(address indexed implementation, string implementationType);

    /**
     * @dev Initialize the factory with implementation contracts
     * @param owner_ The owner of the factory
     */
    constructor(address owner_) {
        _transferOwnership(owner_);

        // Deploy implementations
        tokenImplementation = address(new V4TokenBase());
        securityModuleImplementation = address(new V4SecurityModule());
        distributionModuleImplementation = address(new V4DistributionModule());

        // Create beacons
        tokenBeacon = new UpgradeableBeacon(tokenImplementation);
        securityModuleBeacon = new UpgradeableBeacon(securityModuleImplementation);
        distributionModuleBeacon = new UpgradeableBeacon(distributionModuleImplementation);

        // Transfer beacon ownership to the factory owner
        tokenBeacon.transferOwnership(owner_);
        securityModuleBeacon.transferOwnership(owner_);
        distributionModuleBeacon.transferOwnership(owner_);
    }

    /**
     * @dev Create a new V4 token with default modules
     * @param name Token name
     * @param symbol Token symbol
     * @param initialSupply Initial supply to mint
     * @param owner Owner of the new token
     * @param includeDistribution Whether to include the distribution module
     * @return tokenAddress Address of the newly created token
     */
    function createToken(
        string memory name,
        string memory symbol,
        uint256 initialSupply,
        address owner,
        bool includeDistribution
    ) external returns (address tokenAddress) {
        // Create a new token proxy
        bytes memory tokenData = abi.encodeWithSelector(
            V4TokenBase.initialize.selector,
            name,
            symbol,
            includeDistribution ? 0 : initialSupply, // If using distribution, don't mint initially
            address(this) // Factory temporarily owns the token for setup
        );

        BeaconProxy tokenProxy = new BeaconProxy(
            address(tokenBeacon),
            tokenData
        );
        tokenAddress = address(tokenProxy);

        // Create security module
        bytes memory securityData = abi.encode(owner);

        BeaconProxy securityModuleProxy = new BeaconProxy(
            address(securityModuleBeacon),
            ""
        );
        address securityModuleAddress = address(securityModuleProxy);

        // Initialize the security module
        V4SecurityModule(securityModuleAddress).initialize(tokenAddress, owner);

        // Add security module to token
        IV4TokenBase(tokenAddress).addModule(securityModuleAddress, securityData);

        // Create distribution module if requested
        if (includeDistribution) {
            bytes memory distributionData = abi.encode(owner);

            BeaconProxy distributionModuleProxy = new BeaconProxy(
                address(distributionModuleBeacon),
                ""
            );
            address distributionModuleAddress = address(distributionModuleProxy);

            // Initialize the distribution module
            V4DistributionModule(distributionModuleAddress).initialize(tokenAddress, owner);

            // Add distribution module to token
            IV4TokenBase(tokenAddress).addModule(distributionModuleAddress, distributionData);
        }

        // Transfer token ownership to the security module
        V4TokenBase(tokenAddress).transferOwnership(securityModuleAddress);

        // Register the token
        isV4Token[tokenAddress] = true;
        allTokens.push(tokenAddress);

        emit TokenCreated(tokenAddress, name, symbol, owner);

        return tokenAddress;
    }

    /**
     * @dev Upgrade the token implementation
     * @param newImplementation Address of the new implementation
     */
    function upgradeTokenImplementation(address newImplementation) external onlyOwner {
        require(newImplementation != address(0), "V4Factory: implementation cannot be zero address");
        tokenBeacon.upgradeTo(newImplementation);
        tokenImplementation = newImplementation;

        emit ImplementationUpgraded(newImplementation, "TOKEN");
    }

    /**
     * @dev Upgrade the security module implementation
     * @param newImplementation Address of the new implementation
     */
    function upgradeSecurityModuleImplementation(address newImplementation) external onlyOwner {
        require(newImplementation != address(0), "V4Factory: implementation cannot be zero address");
        securityModuleBeacon.upgradeTo(newImplementation);
        securityModuleImplementation = newImplementation;

        emit ImplementationUpgraded(newImplementation, "SECURITY_MODULE");
    }

    /**
     * @dev Upgrade the distribution module implementation
     * @param newImplementation Address of the new implementation
     */
    function upgradeDistributionModuleImplementation(address newImplementation) external onlyOwner {
        require(newImplementation != address(0), "V4Factory: implementation cannot be zero address");
        distributionModuleBeacon.upgradeTo(newImplementation);
        distributionModuleImplementation = newImplementation;

        emit ImplementationUpgraded(newImplementation, "DISTRIBUTION_MODULE");
    }

    /**
     * @dev Get all tokens created by this factory
     * @return tokens Array of token addresses
     */
    function getAllTokens() external view returns (address[] memory tokens) {
        return allTokens;
    }

    /**
     * @dev Get the number of tokens created by this factory
     * @return count Token count
     */
    function getTokenCount() external view returns (uint256 count) {
        return allTokens.length;
    }
}

src/contracts/V4SecurityModule.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "./interfaces/IV4SecurityModule.sol";
import "./interfaces/IV4Module.sol";
import "./interfaces/IV4TokenBase.sol";

/**
 * @title V4SecurityModule
 * @dev Implementation of security module with multi-sig capabilities
 */
contract V4SecurityModule is 
    Initializable, 
    OwnableUpgradeable, 
    IV4SecurityModule,
    IV4Module
{
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    // Token this module is attached to
    address private _token;

    // Multi-sig configuration
    EnumerableSetUpgradeable.AddressSet private _signers;
    uint256 private _threshold;

    // Proposal tracking
    mapping(bytes32 => address) private _ownershipProposals;
    mapping(bytes32 => mapping(address => bool)) private _confirmations;
    mapping(bytes32 => uint256) private _confirmationCounts;

    // Version info
    string public constant VERSION = "4.0.0";

    // Events
    event OwnershipTransferProposed(bytes32 indexed proposalId, address indexed proposedOwner);
    event OwnershipTransferConfirmed(bytes32 indexed proposalId, address indexed signer);
    event SignerAdded(address indexed signer);
    event SignerRemoved(address indexed signer);
    event ThresholdChanged(uint256 oldThreshold, uint256 newThreshold);
    event EmergencyPaused(address trigger);
    event EmergencyUnpaused(address trigger);

    /**
     * @dev Modifier to check if caller is a signer
     */
    modifier onlySigner() {
        require(_signers.contains(msg.sender), "V4SecurityModule: caller is not a signer");
        _;
    }

    /**
     * @dev Constructor that disables initialization on the implementation contract
     */
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize the security module
     * @param tokenAddress Address of the token this module is attached to
     * @param owner Address of the initial owner
     */
    function initialize(address tokenAddress, address owner) public override initializer {
        __Ownable_init();
        _transferOwnership(owner);

        require(tokenAddress != address(0), "V4SecurityModule: token address cannot be zero");
        _token = tokenAddress;

        _signers.add(owner);
        _threshold = 1;
    }

    /**
     * @dev Initialize the module
     * @param tokenAddress Address of the token this module is attached to
     * @param data Additional initialization data (owner address)
     */
    function initialize(address tokenAddress, bytes calldata data) external override initializer {
        require(data.length == 32, "V4SecurityModule: invalid initialization data");
        address owner = address(uint160(uint256(bytes32(data))));
        initialize(tokenAddress, owner);
    }

    /**
     * @dev Get the type of this module
     * @return moduleType Type identifier for this module
     */
    function getModuleType() external pure override returns (bytes32 moduleType) {
        return keccak256("SECURITY_MODULE");
    }

    /**
     * @dev Check if this module has a specific function
     * @param functionSig Function signature to check for
     * @return hasFunction Whether the module implements the function
     */
    function supportsFunction(bytes4 functionSig) external pure override returns (bool hasFunction) {
        return 
            functionSig == IV4SecurityModule.proposeOwnershipTransfer.selector ||
            functionSig == IV4SecurityModule.confirmOwnershipTransfer.selector ||
            functionSig == IV4SecurityModule.addSigner.selector ||
            functionSig == IV4SecurityModule.removeSigner.selector ||
            functionSig == IV4SecurityModule.setThreshold.selector ||
            functionSig == IV4SecurityModule.isSigner.selector ||
            functionSig == IV4SecurityModule.getThreshold.selector ||
            functionSig == IV4SecurityModule.getSigners.selector ||
            functionSig == IV4SecurityModule.emergencyPause.selector ||
            functionSig == IV4SecurityModule.emergencyUnpause.selector;
    }

    /**
     * @dev Get the token this module is attached to
     * @return tokenAddress Address of the token
     */
    function getToken() external view override returns (address tokenAddress) {
        return _token;
    }

    /**
     * @dev Propose a new owner (multi-sig process step 1)
     * @param newOwner Address of the proposed new owner
     * @return proposalId Identifier for the ownership transfer proposal
     */
    function proposeOwnershipTransfer(address newOwner) external override onlySigner returns (bytes32 proposalId) {
        require(newOwner != address(0), "V4SecurityModule: new owner cannot be zero address");

        // Generate proposal ID
        proposalId = keccak256(abi.encodePacked(newOwner, block.timestamp, msg.sender));

        // Store the proposal
        _ownershipProposals[proposalId] = newOwner;

        // Auto-confirm from proposer
        _confirmations[proposalId][msg.sender] = true;
        _confirmationCounts[proposalId] = 1;

        emit OwnershipTransferProposed(proposalId, newOwner);
        emit OwnershipTransferConfirmed(proposalId, msg.sender);

        // If only one signature needed, execute immediately
        if (_threshold == 1) {
            _executeOwnershipTransfer(proposalId);
        }

        return proposalId;
    }

    /**
     * @dev Confirm an ownership transfer proposal (multi-sig process step 2)
     * @param proposalId Identifier of the proposal to confirm
     * @return success Whether the ownership was successfully transferred
     */
    function confirmOwnershipTransfer(bytes32 proposalId) external override onlySigner returns (bool success) {
        address newOwner = _ownershipProposals[proposalId];
        require(newOwner != address(0), "V4SecurityModule: proposal does not exist");
        require(!_confirmations[proposalId][msg.sender], "V4SecurityModule: already confirmed");

        // Add confirmation
        _confirmations[proposalId][msg.sender] = true;
        _confirmationCounts[proposalId] += 1;

        emit OwnershipTransferConfirmed(proposalId, msg.sender);

        // Check if threshold is reached
        if (_confirmationCounts[proposalId] >= _threshold) {
            _executeOwnershipTransfer(proposalId);
        }

        return true;
    }

    /**
     * @dev Execute an ownership transfer
     * @param proposalId Identifier of the proposal to execute
     */
    function _executeOwnershipTransfer(bytes32 proposalId) private {
        address newOwner = _ownershipProposals[proposalId];
        address currentOwner = owner();

        // Transfer ownership of this module and the token
        _transferOwnership(newOwner);

        // Transfer ownership of the token using module execution rights
        // This is a complex call because we need to call the token from this module
        bytes memory data = abi.encodeWithSignature("transferOwnership(address)", newOwner);
        IV4TokenBase(_token).executeFromModule(_token, data);

        // Clean up proposal
        delete _ownershipProposals[proposalId];

        emit OwnershipTransferred(currentOwner, newOwner);
    }

    /**
     * @dev Add a new signer to the multi-sig configuration
     * @param signer Address to add as a signer
     * @return success Whether the signer was successfully added
     */
    function addSigner(address signer) external override onlyOwner returns (bool success) {
        require(signer != address(0), "V4SecurityModule: signer cannot be zero address");
        require(!_signers.contains(signer), "V4SecurityModule: signer already exists");

        _signers.add(signer);

        emit SignerAdded(signer);
        return true;
    }

    /**
     * @dev Remove a signer from the multi-sig configuration
     * @param signer Address to remove from signers
     * @return success Whether the signer was successfully removed
     */
    function removeSigner(address signer) external override onlyOwner returns (bool success) {
        require(_signers.contains(signer), "V4SecurityModule: signer does not exist");
        require(_signers.length() > _threshold, "V4SecurityModule: would break threshold");
        require(signer != owner(), "V4SecurityModule: cannot remove owner");

        _signers.remove(signer);

        emit SignerRemoved(signer);
        return true;
    }

    /**
     * @dev Set the required number of signatures for operations
     * @param threshold Number of required signatures
     * @return success Whether the threshold was successfully set
     */
    function setThreshold(uint256 threshold) external override onlyOwner returns (bool success) {
        require(threshold > 0, "V4SecurityModule: threshold cannot be zero");
        require(threshold <= _signers.length(), "V4SecurityModule: threshold too high");

        uint256 oldThreshold = _threshold;
        _threshold = threshold;

        emit ThresholdChanged(oldThreshold, threshold);
        return true;
    }

    /**
     * @dev Check if an address is a signer
     * @param account Address to check
     * @return isSigner Whether the address is a signer
     */
    function isSigner(address account) external view override returns (bool isSigner) {
        return _signers.contains(account);
    }

    /**
     * @dev Get the current signature threshold
     * @return threshold The current signature threshold
     */
    function getThreshold() external view override returns (uint256 threshold) {
        return _threshold;
    }

    /**
     * @dev Get all current signers
     * @return signers Array of current signer addresses
     */
    function getSigners() external view override returns (address[] memory signers) {
        uint256 length = _signers.length();
        signers = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            signers[i] = _signers.at(i);
        }

        return signers;
    }

    /**
     * @dev Emergency pause for the token
     * @return success Whether the pause was successful
     */
    function emergencyPause() external override onlySigner returns (bool success) {
        // Call pause on the token using module execution rights
        bytes memory data = abi.encodeWithSignature("pause()");
        IV4TokenBase(_token).executeFromModule(_token, data);

        emit EmergencyPaused(msg.sender);
        return true;
    }

    /**
     * @dev Unpause the token after emergency
     * @return success Whether the unpause was successful
     */
    function emergencyUnpause() external override onlySigner returns (bool success) {
        // Call unpause on the token using module execution rights
        bytes memory data = abi.encodeWithSignature("unpause()");
        IV4TokenBase(_token).executeFromModule(_token, data);

        emit EmergencyUnpaused(msg.sender);
        return true;
    }
}

src/contracts/V4TokenBase.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "./interfaces/IV4TokenBase.sol";
import "./interfaces/IV4Module.sol";

/**
 * @title V4TokenBase
 * @dev Base implementation for V4 tokens with modular architecture
 */
contract V4TokenBase is 
    Initializable, 
    ERC20Upgradeable, 
    PausableUpgradeable, 
    OwnableUpgradeable,
    IV4TokenBase
{
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    // Version info
    string public constant VERSION = "4.0.0";

    // Module storage
    EnumerableSetUpgradeable.AddressSet private _modules;
    mapping(bytes32 => address) private _moduleByType;

    // Events
    event ModuleAdded(address indexed moduleAddress, bytes32 indexed moduleType);
    event ModuleRemoved(address indexed moduleAddress, bytes32 indexed moduleType);
    event ModuleExecution(address indexed moduleAddress, address indexed target, bool success);

    // Errors
    error InvalidModule();
    error ModuleExists();
    error ModuleDoesNotExist();
    error ModuleExecutionFailed();
    error CoreModuleRequired();
    error PermissionDenied();

    /**
     * @dev Modifier to check if caller is an active module
     */
    modifier onlyModule() {
        if (!_modules.contains(msg.sender)) {
            revert PermissionDenied();
        }
        _;
    }

    /**
     * @dev Constructor that disables initialization on the implementation contract
     */
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize the token with basic parameters
     * @param name_ Token name
     * @param symbol_ Token symbol
     * @param initialSupply Initial supply to mint
     * @param owner_ Address that will receive the initial supply and become the owner
     */
    function initialize(
        string memory name_,
        string memory symbol_,
        uint256 initialSupply,
        address owner_
    ) public initializer {
        __ERC20_init(name_, symbol_);
        __Pausable_init();
        __Ownable_init();
        _transferOwnership(owner_);

        if (initialSupply > 0) {
            _mint(owner_, initialSupply);
        }
    }

    /**
     * @dev Add a module to the token
     * @param moduleAddress Address of the module to add
     * @param data Initialization data for the module
     * @return success Whether the module was successfully added
     */
    function addModule(address moduleAddress, bytes calldata data) external override onlyOwner returns (bool success) {
        // Validate the module
        if (moduleAddress == address(0)) {
            revert InvalidModule();
        }

        if (_modules.contains(moduleAddress)) {
            revert ModuleExists();
        }

        // Try to initialize the module
        try IV4Module(moduleAddress).initialize(address(this), data) {
            // Get the module type
            bytes32 moduleType = IV4Module(moduleAddress).getModuleType();

            // Add the module to our tracking
            _modules.add(moduleAddress);

            // Register the module type if not already registered
            if (_moduleByType[moduleType] == address(0)) {
                _moduleByType[moduleType] = moduleAddress;
            }

            emit ModuleAdded(moduleAddress, moduleType);
            return true;
        } catch {
            revert InvalidModule();
        }
    }

    /**
     * @dev Remove a module from the token
     * @param moduleAddress Address of the module to remove
     * @return success Whether the module was successfully removed
     */
    function removeModule(address moduleAddress) external override onlyOwner returns (bool success) {
        if (!_modules.contains(moduleAddress)) {
            revert ModuleDoesNotExist();
        }

        // Get the module type before removing
        bytes32 moduleType = IV4Module(moduleAddress).getModuleType();

        // If this is a core module type, don't allow removal
        if (moduleType == keccak256("SECURITY_MODULE")) {
            revert CoreModuleRequired();
        }

        // Remove the module
        _modules.remove(moduleAddress);

        // If this was the registered module for a type, clear that registration
        if (_moduleByType[moduleType] == moduleAddress) {
            delete _moduleByType[moduleType];
        }

        emit ModuleRemoved(moduleAddress, moduleType);
        return true;
    }

    /**
     * @dev Check if a module is active
     * @param moduleAddress Address of the module to check
     * @return isActive Whether the module is active
     */
    function isModuleActive(address moduleAddress) external view override returns (bool isActive) {
        return _modules.contains(moduleAddress);
    }

    /**
     * @dev Get all active modules
     * @return modules Array of active module addresses
     */
    function getModules() external view override returns (address[] memory modules) {
        uint256 length = _modules.length();
        modules = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            modules[i] = _modules.at(i);
        }

        return modules;
    }

    /**
     * @dev Find a module by its type
     * @param moduleType Type of module to find
     * @return moduleAddress Address of the module, or address(0) if not found
     */
    function getModuleByType(bytes32 moduleType) external view returns (address moduleAddress) {
        return _moduleByType[moduleType];
    }

    /**
     * @dev Execute a function call from a module
     * @param target Target address for the call
     * @param data Call data
     * @return success Whether the call was successful
     * @return returnData Data returned by the call
     */
    function executeFromModule(address target, bytes calldata data) external override onlyModule returns (bool success, bytes memory returnData) {
        (success, returnData) = target.call(data);

        emit ModuleExecution(msg.sender, target, success);

        if (!success) {
            revert ModuleExecutionFailed();
        }

        return (success, returnData);
    }

    /**
     * @dev Pause the token
     */
    function pause() external override onlyModule {
        _pause();
    }

    /**
     * @dev Unpause the token
     */
    function unpause() external override onlyModule {
        _unpause();
    }

    /**
     * @dev Override for token transfer to respect pause state
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        super._beforeTokenTransfer(from, to, amount);

        require(!paused(), "V4TokenBase: token transfer while paused");
    }

    // Override transferOwnership to satisfy both interfaces
    function transferOwnership(address newOwner) public override(OwnableUpgradeable, IV4TokenBase) onlyOwner {
        super.transferOwnership(newOwner);
    }
}

