pragma solidity 0.8.22;

import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "./interfaces/IV4SecurityModule.sol";
import "./interfaces/IV4Module.sol";
import "./interfaces/IV4TokenBase.sol";

/**
 * @title V4SecurityModule
 * @dev Implementation of security module with multi-sig capabilities
 */
contract V4SecurityModule is 
    Initializable, 
    OwnableUpgradeable, 
    IV4SecurityModule,
    IV4Module
{
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    // Token this module is attached to
    address private _token;

    // Multi-sig configuration
    EnumerableSetUpgradeable.AddressSet private _signers;
    uint256 private _threshold;

    // Proposal tracking
    mapping(bytes32 => address) private _ownershipProposals;
    mapping(bytes32 => mapping(address => bool)) private _confirmations;
    mapping(bytes32 => uint256) private _confirmationCounts;

    // Version info
    string public constant VERSION = "4.0.0";

    // Events
    event OwnershipTransferProposed(bytes32 indexed proposalId, address indexed proposedOwner);
    event OwnershipTransferConfirmed(bytes32 indexed proposalId, address indexed signer);
    event SignerAdded(address indexed signer);
    event SignerRemoved(address indexed signer);
    event ThresholdChanged(uint256 oldThreshold, uint256 newThreshold);
    event EmergencyPaused(address trigger);
    event EmergencyUnpaused(address trigger);

    /**
     * @dev Modifier to check if caller is a signer
     */
    modifier onlySigner() {
        require(_signers.contains(msg.sender), "V4SecurityModule: caller is not a signer");
        _;
    }

    /**
     * @dev Constructor that disables initialization on the implementation contract
     */
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize the security module
     * @param tokenAddress Address of the token this module is attached to
     * @param owner Address of the initial owner
     */
    function initialize(address tokenAddress, address owner) public override initializer {
        __Ownable_init();
        _transferOwnership(owner);

        require(tokenAddress != address(0), "V4SecurityModule: token address cannot be zero");
        _token = tokenAddress;

        _signers.add(owner);
        _threshold = 1;
    }

    /**
     * @dev Initialize the module
     * @param tokenAddress Address of the token this module is attached to
     * @param data Additional initialization data (owner address)
     */
    function initialize(address tokenAddress, bytes calldata data) external override initializer {
        require(data.length == 32, "V4SecurityModule: invalid initialization data");
        address owner = address(uint160(uint256(bytes32(data))));
        initialize(tokenAddress, owner);
    }

    /**
     * @dev Get the type of this module
     * @return moduleType Type identifier for this module
     */
    function getModuleType() external pure override returns (bytes32 moduleType) {
        return keccak256("SECURITY_MODULE");
    }

    /**
     * @dev Check if this module has a specific function
     * @param functionSig Function signature to check for
     * @return hasFunction Whether the module implements the function
     */
    function supportsFunction(bytes4 functionSig) external pure override returns (bool hasFunction) {
        return 
            functionSig == IV4SecurityModule.proposeOwnershipTransfer.selector ||
            functionSig == IV4SecurityModule.confirmOwnershipTransfer.selector ||
            functionSig == IV4SecurityModule.addSigner.selector ||
            functionSig == IV4SecurityModule.removeSigner.selector ||
            functionSig == IV4SecurityModule.setThreshold.selector ||
            functionSig == IV4SecurityModule.isSigner.selector ||
            functionSig == IV4SecurityModule.getThreshold.selector ||
            functionSig == IV4SecurityModule.getSigners.selector ||
            functionSig == IV4SecurityModule.emergencyPause.selector ||
            functionSig == IV4SecurityModule.emergencyUnpause.selector;
    }

    /**
     * @dev Get the token this module is attached to
     * @return tokenAddress Address of the token
     */
    function getToken() external view override returns (address tokenAddress) {
        return _token;
    }

    /**
     * @dev Propose a new owner (multi-sig process step 1)
     * @param newOwner Address of the proposed new owner
     * @return proposalId Identifier for the ownership transfer proposal
     */
    function proposeOwnershipTransfer(address newOwner) external override onlySigner returns (bytes32 proposalId) {
        require(newOwner != address(0), "V4SecurityModule: new owner cannot be zero address");

        // Generate proposal ID
        proposalId = keccak256(abi.encodePacked(newOwner, block.timestamp, msg.sender));

        // Store the proposal
        _ownershipProposals[proposalId] = newOwner;

        // Auto-confirm from proposer
        _confirmations[proposalId][msg.sender] = true;
        _confirmationCounts[proposalId] = 1;

        emit OwnershipTransferProposed(proposalId, newOwner);
        emit OwnershipTransferConfirmed(proposalId, msg.sender);

        // If only one signature needed, execute immediately
        if (_threshold == 1) {
            _executeOwnershipTransfer(proposalId);
        }

        return proposalId;
    }

    /**
     * @dev Confirm an ownership transfer proposal (multi-sig process step 2)
     * @param proposalId Identifier of the proposal to confirm
     * @return success Whether the ownership was successfully transferred
     */
    function confirmOwnershipTransfer(bytes32 proposalId) external override onlySigner returns (bool success) {
        address newOwner = _ownershipProposals[proposalId];
        require(newOwner != address(0), "V4SecurityModule: proposal does not exist");
        require(!_confirmations[proposalId][msg.sender], "V4SecurityModule: already confirmed");

        // Add confirmation
        _confirmations[proposalId][msg.sender] = true;
        _confirmationCounts[proposalId] += 1;

        emit OwnershipTransferConfirmed(proposalId, msg.sender);

        // Check if threshold is reached
        if (_confirmationCounts[proposalId] >= _threshold) {
            _executeOwnershipTransfer(proposalId);
        }

        return true;
    }

    /**
     * @dev Execute an ownership transfer
     * @param proposalId Identifier of the proposal to execute
     */
    function _executeOwnershipTransfer(bytes32 proposalId) private {
        address newOwner = _ownershipProposals[proposalId];
        address currentOwner = owner();

        // Transfer ownership of this module and the token
        _transferOwnership(newOwner);

        // Transfer ownership of the token using module execution rights
        // This is a complex call because we need to call the token from this module
        bytes memory data = abi.encodeWithSignature("transferOwnership(address)", newOwner);
        IV4TokenBase(_token).executeFromModule(_token, data);

        // Clean up proposal
        delete _ownershipProposals[proposalId];

        emit OwnershipTransferred(currentOwner, newOwner);
    }

    /**
     * @dev Add a new signer to the multi-sig configuration
     * @param signer Address to add as a signer
     * @return success Whether the signer was successfully added
     */
    function addSigner(address signer) external override onlyOwner returns (bool success) {
        require(signer != address(0), "V4SecurityModule: signer cannot be zero address");
        require(!_signers.contains(signer), "V4SecurityModule: signer already exists");

        _signers.add(signer);

        emit SignerAdded(signer);
        return true;
    }

    /**
     * @dev Remove a signer from the multi-sig configuration
     * @param signer Address to remove from signers
     * @return success Whether the signer was successfully removed
     */
    function removeSigner(address signer) external override onlyOwner returns (bool success) {
        require(_signers.contains(signer), "V4SecurityModule: signer does not exist");
        require(_signers.length() > _threshold, "V4SecurityModule: would break threshold");
        require(signer != owner(), "V4SecurityModule: cannot remove owner");

        _signers.remove(signer);

        emit SignerRemoved(signer);
        return true;
    }

    /**
     * @dev Set the required number of signatures for operations
     * @param threshold Number of required signatures
     * @return success Whether the threshold was successfully set
     */
    function setThreshold(uint256 threshold) external override onlyOwner returns (bool success) {
        require(threshold > 0, "V4SecurityModule: threshold cannot be zero");
        require(threshold <= _signers.length(), "V4SecurityModule: threshold too high");

        uint256 oldThreshold = _threshold;
        _threshold = threshold;

        emit ThresholdChanged(oldThreshold, threshold);
        return true;
    }

    /**
     * @dev Check if an address is a signer
     * @param account Address to check
     * @return isSigner Whether the address is a signer
     */
    function isSigner(address account) external view override returns (bool isSigner) {
        return _signers.contains(account);
    }

    /**
     * @dev Get the current signature threshold
     * @return threshold The current signature threshold
     */
    function getThreshold() external view override returns (uint256 threshold) {
        return _threshold;
    }

    /**
     * @dev Get all current signers
     * @return signers Array of current signer addresses
     */
    function getSigners() external view override returns (address[] memory signers) {
        uint256 length = _signers.length();
        signers = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            signers[i] = _signers.at(i);
        }

        return signers;
    }

    /**
     * @dev Emergency pause for the token
     * @return success Whether the pause was successful
     */
    function emergencyPause() external override onlySigner returns (bool success) {
        // Call pause on the token using module execution rights
        bytes memory data = abi.encodeWithSignature("pause()");
        IV4TokenBase(_token).executeFromModule(_token, data);

        emit EmergencyPaused(msg.sender);
        return true;
    }

    /**
     * @dev Unpause the token after emergency
     * @return success Whether the unpause was successful
     */
    function emergencyUnpause() external override onlySigner returns (bool success) {
        // Call unpause on the token using module execution rights
        bytes memory data = abi.encodeWithSignature("unpause()");
        IV4TokenBase(_token).executeFromModule(_token, data);

        emit EmergencyUnpaused(msg.sender);
        return true;
    }
} ","keccak256":"0x6540d2751287353bbcffdf2b03a9108544004a3d073d4e2c231d453d019b0127","license":"MIT"},"src/contracts/V4TokenBase.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/utils/structs/EnumerableSetUpgradeable.sol";
import "./interfaces/IV4TokenBase.sol";
import "./interfaces/IV4Module.sol";

/**
 * @title V4TokenBase
 * @dev Base implementation for V4 tokens with modular architecture
 */
contract V4TokenBase is 
    Initializable, 
    ERC20Upgradeable, 
    PausableUpgradeable, 
    OwnableUpgradeable,
    IV4TokenBase
{
    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    // Version info
    string public constant VERSION = "4.0.0";

    // Module storage
    EnumerableSetUpgradeable.AddressSet private _modules;
    mapping(bytes32 => address) private _moduleByType;

    // Events
    event ModuleAdded(address indexed moduleAddress, bytes32 indexed moduleType);
    event ModuleRemoved(address indexed moduleAddress, bytes32 indexed moduleType);
    event ModuleExecution(address indexed moduleAddress, address indexed target, bool success);

    // Errors
    error InvalidModule();
    error ModuleExists();
    error ModuleDoesNotExist();
    error ModuleExecutionFailed();
    error CoreModuleRequired();
    error PermissionDenied();

    /**
     * @dev Modifier to check if caller is an active module
     */
    modifier onlyModule() {
        if (!_modules.contains(msg.sender)) {
            revert PermissionDenied();
        }
        _;
    }

    /**
     * @dev Constructor that disables initialization on the implementation contract
     */
    constructor() {
        _disableInitializers();
    }

    /**
     * @dev Initialize the token with basic parameters
     * @param name_ Token name
     * @param symbol_ Token symbol
     * @param initialSupply Initial supply to mint
     * @param owner_ Address that will receive the initial supply and become the owner
     */
    function initialize(
        string memory name_,
        string memory symbol_,
        uint256 initialSupply,
        address owner_
    ) public initializer {
        __ERC20_init(name_, symbol_);
        __Pausable_init();
        __Ownable_init();
        _transferOwnership(owner_);

        if (initialSupply > 0) {
            _mint(owner_, initialSupply);
        }
    }

    /**
     * @dev Add a module to the token
     * @param moduleAddress Address of the module to add
     * @param data Initialization data for the module
     * @return success Whether the module was successfully added
     */
    function addModule(address moduleAddress, bytes calldata data) external override onlyOwner returns (bool success) {
        // Validate the module
        if (moduleAddress == address(0)) {
            revert InvalidModule();
        }

        if (_modules.contains(moduleAddress)) {
            revert ModuleExists();
        }

        // Try to initialize the module
        try IV4Module(moduleAddress).initialize(address(this), data) {
            // Get the module type
            bytes32 moduleType = IV4Module(moduleAddress).getModuleType();

            // Add the module to our tracking
            _modules.add(moduleAddress);

            // Register the module type if not already registered
            if (_moduleByType[moduleType] == address(0)) {
                _moduleByType[moduleType] = moduleAddress;
            }

            emit ModuleAdded(moduleAddress, moduleType);
            return true;
        } catch {
            revert InvalidModule();
        }
    }

    /**
     * @dev Remove a module from the token
     * @param moduleAddress Address of the module to remove
     * @return success Whether the module was successfully removed
     */
    function removeModule(address moduleAddress) external override onlyOwner returns (bool success) {
        if (!_modules.contains(moduleAddress)) {
            revert ModuleDoesNotExist();
        }

        // Get the module type before removing
        bytes32 moduleType = IV4Module(moduleAddress).getModuleType();

        // If this is a core module type, don't allow removal
        if (moduleType == keccak256("SECURITY_MODULE")) {
            revert CoreModuleRequired();
        }

        // Remove the module
        _modules.remove(moduleAddress);

        // If this was the registered module for a type, clear that registration
        if (_moduleByType[moduleType] == moduleAddress) {
            delete _moduleByType[moduleType];
        }

        emit ModuleRemoved(moduleAddress, moduleType);
        return true;
    }

    /**
     * @dev Check if a module is active
     * @param moduleAddress Address of the module to check
     * @return isActive Whether the module is active
     */
    function isModuleActive(address moduleAddress) external view override returns (bool isActive) {
        return _modules.contains(moduleAddress);
    }

    /**
     * @dev Get all active modules
     * @return modules Array of active module addresses
     */
    function getModules() external view override returns (address[] memory modules) {
        uint256 length = _modules.length();
        modules = new address[](length);

        for (uint256 i = 0; i < length; i++) {
            modules[i] = _modules.at(i);
        }

        return modules;
    }

    /**
     * @dev Find a module by its type
     * @param moduleType Type of module to find
     * @return moduleAddress Address of the module, or address(0) if not found
     */
    function getModuleByType(bytes32 moduleType) external view returns (address moduleAddress) {
        return _moduleByType[moduleType];
    }

    /**
     * @dev Execute a function call from a module
     * @param target Target address for the call
     * @param data Call data
     * @return success Whether the call was successful
     * @return returnData Data returned by the call
     */
    function executeFromModule(address target, bytes calldata data) external override onlyModule returns (bool success, bytes memory returnData) {
        (success, returnData) = target.call(data);

        emit ModuleExecution(msg.sender, target, success);

        if (!success) {
            revert ModuleExecutionFailed();
        }

        return (success, returnData);
    }

    /**
     * @dev Pause the token
     */
    function pause() external override onlyModule {
        _pause();
    }

    /**
     * @dev Unpause the token
     */
    function unpause() external override onlyModule {
        _unpause();
    }

    /**
     * @dev Override for token transfer to respect pause state
     */
    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        super._beforeTokenTransfer(from, to, amount);

        require(!paused(), "V4TokenBase: token transfer while paused");
    }

    // Override transferOwnership to satisfy both interfaces
    function transferOwnership(address newOwner) public override(OwnableUpgradeable, IV4TokenBase) onlyOwner {
        super.transferOwnership(newOwner);
    }
} ","keccak256":"0xd461af1b8499c77138b149a56d419fd4dde07339a0a436309c52943ba10665d0","license":"MIT"},"src/contracts/interfaces/IV4DistributionModule.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

/**
 * @title IV4DistributionModule
 * @dev Interface for the distribution module with allocation capabilities
 */
interface IV4DistributionModule {
    /**
     * @dev Struct for each allocation entry
     */
    struct Allocation {
        address wallet;
        uint256 amount;
        string label;
        bool locked;
        uint256 unlockTime;
    }

    /**
     * @dev Initialize the distribution module
     * @param tokenAddress Address of the token this module is attached to
     * @param owner Address of the initial owner
     */
    function initialize(address tokenAddress, address owner) external;

    /**
     * @dev Add a new allocation
     * @param wallet Recipient wallet address
     * @param amount Amount of tokens to allocate
     * @param label Optional label for this allocation (e.g. "Team", "Marketing")
     * @param locked Whether the allocation should be time-locked
     * @param unlockTime Timestamp when tokens become unlocked (0 if not locked)
     * @return success Whether the allocation was successfully added
     */
    function addAllocation(
        address wallet, 
        uint256 amount, 
        string calldata label,
        bool locked,
        uint256 unlockTime
    ) external returns (bool success);

    /**
     * @dev Add multiple allocations at once
     * @param wallets Array of recipient wallet addresses
     * @param amounts Array of token amounts
     * @param labels Array of labels
     * @param lockStatus Array of lock statuses
     * @param unlockTimes Array of unlock times
     * @return success Whether all allocations were successfully added
     */
    function addMultipleAllocations(
        address[] calldata wallets,
        uint256[] calldata amounts,
        string[] calldata labels,
        bool[] calldata lockStatus,
        uint256[] calldata unlockTimes
    ) external returns (bool success);

    /**
     * @dev Apply a preset distribution
     * @param presetId Identifier for the distribution preset
     * @param totalSupply Total supply to distribute
     * @return success Whether the preset was successfully applied
     */
    function applyPreset(
        uint256 presetId,
        uint256 totalSupply
    ) external returns (bool success);

    /**
     * @dev Remove an allocation
     * @param wallet Recipient wallet address to remove
     * @return success Whether the allocation was successfully removed
     */
    function removeAllocation(address wallet) external returns (bool success);

    /**
     * @dev Modify an existing allocation
     * @param wallet Recipient wallet address
     * @param amount New amount of tokens
     * @param label New label
     * @param locked New lock status
     * @param unlockTime New unlock time
     * @return success Whether the allocation was successfully modified
     */
    function modifyAllocation(
        address wallet,
        uint256 amount,
        string calldata label,
        bool locked,
        uint256 unlockTime
    ) external returns (bool success);

    /**
     * @dev Get all allocations
     * @return allocations Array of allocation details
     */
    function getAllAllocations() external view returns (Allocation[] memory allocations);

    /**
     * @dev Get allocation for a specific wallet
     * @param wallet Recipient wallet address
     * @return allocation The allocation details
     */
    function getAllocation(address wallet) external view returns (Allocation memory allocation);

    /**
     * @dev Get available presets
     * @return presetIds Array of preset IDs
     * @return presetNames Array of preset names
     */
    function getAvailablePresets() external view returns (uint256[] memory presetIds, string[] memory presetNames);

    /**
     * @dev Get preset details
     * @param presetId Identifier for the preset
     * @return walletRatios Array of ratios for different wallet types
     * @return labels Array of labels for the wallet types
     */
    function getPresetDetails(uint256 presetId) external view returns (uint256[] memory walletRatios, string[] memory labels);

    /**
     * @dev Execute the distribution
     * @return success Whether the distribution was successfully executed
     */
    function executeDistribution() external returns (bool success);

    /**
     * @dev Unlock tokens for a wallet if the unlock time has passed
     * @param wallet Address of the wallet to unlock tokens for
     * @return success Whether the tokens were successfully unlocked
     */
    function unlockTokens(address wallet) external returns (bool success);
} ","keccak256":"0x17d078895828a8b51e8b68fd830239154361a67e7c3bebb9a9bc7438d657183d","license":"MIT"},"src/contracts/interfaces/IV4Module.sol":{"content":"// SPDX-License-Identifier: MIT
