l","name":"success","type":"bool"}],"stateMutability":"nonpayable","type":"function"}],"devdoc":{"details":"Interface for the V4 Token Factory","kind":"dev","methods":{"createToken(string,string,uint256,address)":{"details":"Creates a new token with basic configuration","params":{"initialSupply":"Initial supply to mint","name":"Token name","owner":"Address that will receive the initial supply and become the owner","symbol":"Token symbol"},"returns":{"tokenAddress":"Address of the newly created token"}},"createTokenWithSecurity(string,string,uint256,address,bool,address[],uint256)":{"details":"Creates a new token with an optional security module","params":{"additionalSigners":"Additional signers for the security module (if enabled)","initialSupply":"Initial supply to mint","name":"Token name","owner":"Address that will receive the initial supply and become the owner","securityEnabled":"Whether to enable the security module","symbol":"Token symbol","threshold":"Signature threshold for the security module (if enabled)"},"returns":{"tokenAddress":"Address of the newly created token"}},"getAllTokens()":{"details":"Get all tokens created by this factory","returns":{"tokens":"Array of token addresses"}},"getSecurityModuleImplementation()":{"details":"Get the implementation address for the security module","returns":{"implementation":"Address of the security module implementation"}},"getTokenImplementation()":{"details":"Get the implementation address for the token","returns":{"implementation":"Address of the token implementation"}},"upgradeSecurityModuleImplementation(address)":{"details":"Upgrades the security module implementation","params":{"newImplementation":"Address of the new implementation"},"returns":{"success":"Whether the upgrade was successful"}},"upgradeTokenImplementation(address)":{"details":"Upgrades the token implementation","params":{"newImplementation":"Address of the new implementation"},"returns":{"success":"Whether the upgrade was successful"}}},"title":"IV4Factory","version":1},"userdoc":{"kind":"user","methods":{},"version":1}},"settings":{"compilationTarget":{"src/contracts/interfaces/IV4Factory.sol":"IV4Factory"},"evmVersion":"paris","libraries":{},"metadata":{"bytecodeHash":"ipfs","useLiteralContent":true},"optimizer":{"enabled":true,"runs":200},"remappings":[],"viaIR":true},"sources":{"src/contracts/interfaces/IV4Factory.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity 0.8.22;

/**
 * @title IV4Factory
 * @dev Interface for the V4 Token Factory
 */
interface IV4Factory {
    /**
     * @dev Creates a new token with basic configuration
     * @param name Token name
     * @param symbol Token symbol
     * @param initialSupply Initial supply to mint
     * @param owner Address that will receive the initial supply and become the owner
     * @return tokenAddress Address of the newly created token
     */
    function createToken(
        string memory name,
        string memory symbol,
        uint256 initialSupply,
        address owner
    ) external returns (address tokenAddress);

    /**
     * @dev Creates a new token with an optional security module
     * @param name Token name
     * @param symbol Token symbol
     * @param initialSupply Initial supply to mint
     * @param owner Address that will receive the initial supply and become the owner
     * @param securityEnabled Whether to enable the security module
     * @param additionalSigners Additional signers for the security module (if enabled)
     * @param threshold Signature threshold for the security module (if enabled)
     * @return tokenAddress Address of the newly created token
     */
    function createTokenWithSecurity(
        string memory name,
        string memory symbol,
        uint256 initialSupply,
        address owner,
        bool securityEnabled,
        address[] memory additionalSigners,
        uint256 threshold
    ) external returns (address tokenAddress);

    /**
     * @dev Get the implementation address for the token
     * @return implementation Address of the token implementation
     */
    function getTokenImplementation() external view returns (address implementation);

    /**
     * @dev Get the implementation address for the security module
     * @return implementation Address of the security module implementation
     */
    function getSecurityModuleImplementation() external view returns (address implementation);

    /**
     * @dev Get all tokens created by this factory
     * @return tokens Array of token addresses
     */
    function getAllTokens() external view returns (address[] memory tokens);

    /**
     * @dev Upgrades the token implementation
     * @param newImplementation Address of the new implementation
     * @return success Whether the upgrade was successful
     */
    function upgradeTokenImplementation(address newImplementation) external returns (bool success);

    /**
     * @dev Upgrades the security module implementation
     * @param newImplementation Address of the new implementation
     * @return success Whether the upgrade was successful
     */
    function upgradeSecurityModuleImplementation(address newImplementation) external returns (bool success);
} ","keccak256":"0x8496c5d3266f1851f27ae96456d7dfc1b93cc41ec31fbc4111965d9b6b3b86ad","license":"MIT"}},"version":1}

